﻿import React, { useEffect, useState, useCallback, useRef } from 'react';
import { eli5Term } from '../services/ai';
import { Events, track } from '../analytics/track';
import { safeLocalStorage } from '../lib/safeStorage';

const KEYWORDS = ['持分', '使用分區', '公設比', '地上權', '都更', '容積率'];

export const ELI5Tooltip: React.FC<{ text: string }> = ({ text }) => {
  const [open, setOpen] = useState(false);
  const [ans, setAns] = useState<string>('');
  const [loading, setLoading] = useState(false);
  const hasSuggestedRef = useRef(false);

  const fetchAns = useCallback(async () => {
    if (ans) return;
    setLoading(true);
    try {
      const out = await eli5Term(text);
      setAns(out);
      track(Events.ELI5Open, { term: text });
    } finally {
      setLoading(false);
    }
  }, [ans, text]);

  // 每日一次的「主動建議」：遇到關鍵詞自動開啟
  useEffect(() => {
    if (hasSuggestedRef.current) return;

    const today = new Date().toISOString().slice(0, 10);
    const k = `mai-eli5-suggest-${today}`;
    const shouldSuggest = KEYWORDS.some((w) => text.includes(w)) && !safeLocalStorage.getItem(k);
    if (shouldSuggest) {
      hasSuggestedRef.current = true;
      safeLocalStorage.setItem(k, '1');
      setOpen(true);
      fetchAns();
    }
  }, [text, fetchAns]);

  const toggle = async () => {
    const next = !open;
    setOpen(next);
    if (next) await fetchAns();
  };

  return (
    <span className="relative inline-flex items-center">
      <button
        aria-label="白話解釋"
        onClick={toggle}
        className="ml-1.5 h-[18px] w-[18px] cursor-pointer rounded-full border border-[#C9D5FF] bg-[#F5F8FF] text-xs text-[#1749D7]"
      >
        ?
      </button>
      {open && (
        <div className="absolute left-0 top-[120%] z-50 w-[280px] rounded-lg border border-[#E6ECFF] bg-white p-2.5 shadow-[0_10px_24px_rgba(0,0,0,0.08)]">
          <div className="mb-1.5 text-xs text-[#6b7280]">
            白話解釋（僅供參考，非法律意見）
          </div>
          <div className="whitespace-pre-wrap">{loading ? '生成中…' : ans}</div>
        </div>
      )}
    </span>
  );
};

﻿/**
 * MaiMai 慶祝動畫 Hook
 * @description 使用 canvas-confetti 實現高效能撒花動畫
 */

import { useCallback, useEffect, useRef } from 'react';
import confetti from 'canvas-confetti';

interface UseConfettiOptions {
  /** 彩帶數量 */
  particleCount?: number;
  /** 擴散角度 (度) */
  spread?: number;
  /** 起始位置 { x: 0-1, y: 0-1 } */
  origin?: { x: number; y: number };
  /** 彩帶顏色 */
  colors?: string[];
}

/**
 * 慶祝動畫 Hook
 *
 * @description NASA-grade 效能優化：
 * - 使用 useRef 持有 Canvas 實例，避免重複初始化
 * - 使用 useCallback 包裝 fire 函數，避免重複建立
 * - 組件卸載時自動清理
 *
 * @example
 * ```tsx
 * function MaiMai() {
 *   const { fireConfetti, ConfettiCanvas } = useConfetti();
 *
 *   return (
 *     <div>
 *       <button onClick={fireConfetti}>慶祝！</button>
 *       <ConfettiCanvas />
 *     </div>
 *   );
 * }
 * ```
 */
export function useConfetti(options: UseConfettiOptions = {}) {
  const {
    particleCount = 100,
    spread = 70,
    origin = { x: 0.5, y: 0.6 },
    colors = ['#FBBF24', '#F472B6', '#38BDF8', '#A78BFA', '#22C55E'],
  } = options;

  const canvasRef = useRef<HTMLCanvasElement | null>(null);
  const confettiInstance = useRef<ReturnType<typeof confetti.create> | null>(null);

  // 初始化 confetti 實例
  useEffect(() => {
    if (canvasRef.current && !confettiInstance.current) {
      confettiInstance.current = confetti.create(canvasRef.current, {
        resize: true,
        useWorker: true,
      });
    }

    // 清理函數
    return () => {
      if (confettiInstance.current) {
        confettiInstance.current.reset();
      }
    };
  }, []);

  // 觸發撒花動畫
  const fireConfetti = useCallback(() => {
    if (!confettiInstance.current) return;

    confettiInstance.current({
      particleCount,
      spread,
      origin,
      colors,
      disableForReducedMotion: true,
    });
  }, [particleCount, spread, origin, colors]);

  // Canvas 組件 (useMemo 優於 useCallback,因為返回 JSX 而非函數)
  const ConfettiCanvas = useCallback((): JSX.Element => {
    return (
      <canvas
        ref={canvasRef}
        className="pointer-events-none fixed inset-0 size-full z-[9999]"
      />
    );
  }, []);

  return {
    fireConfetti,
    ConfettiCanvas,
  } as const;
}

export default useConfetti;

﻿import { useState } from 'react';
import { ShieldCheck, ArrowRight, ChevronDown, ChevronUp } from 'lucide-react';
import MascotHouse from '../../../components/MascotHouse';
import { HERO_STEPS } from '../../../constants/data';
import { HomeCard } from '../components/HomeCard';
import { HeroStep } from '../components/HeroStep';

/** 手機版預設顯示的步驟數量 */
const MOBILE_VISIBLE_STEPS = 2;

export default function HeroAssure() {
  const [isExpanded, setIsExpanded] = useState(false);

  return (
    <HomeCard variant="hero" className="group/container relative overflow-hidden">
      {/* Header Area */}
      <div className="relative z-10 mb-10 flex flex-col items-center gap-6 md:flex-row md:gap-10">
        {/* Mascot: Wireframe House */}
        <div className="relative h-32 w-28 shrink-0">
          <MascotHouse />
        </div>

        {/* Text Content */}
        <div className="flex-1 text-center md:text-left">
          <div className="border-brand-700/20 mb-3 inline-flex items-center gap-2 rounded-full border bg-brand-50 px-3 py-1 text-xs font-bold text-brand-700">
            <ShieldCheck size={14} />
            <span>全程透明．安心留痕</span>
          </div>
          <h3 className="mb-3 text-2xl font-black tracking-tight text-brand-700 md:text-3xl">
            安心留痕保障
          </h3>
          <p className="max-w-2xl text-sm font-medium leading-relaxed text-[var(--text-secondary)] md:text-base">
            介紹改從第一次電話聯絡開始，買賣雙方的每一通聯絡紀錄、每一句承諾與每項協議，
            都會經過雙方確認並完整留痕。
            <br className="hidden md:block" />
            讓整個交易過程都有跡可循，保障雙方權益，直到圓滿交屋。
          </p>
        </div>

        <a
          href="/#policy"
          className="hidden shrink-0 items-center gap-2 rounded-xl border-2 border-brand-700 bg-white px-5 py-2.5 text-sm font-bold text-brand-700 shadow-sm transition-all hover:bg-brand-700 hover:text-white md:flex"
        >
          履保規範 <ArrowRight size={16} />
        </a>
      </div>

      {/* Process Timeline */}
      <div className="relative mt-2 pl-2 md:mt-4 md:pl-0">
        {/* Connecting Line (Desktop) */}
        <div className="absolute left-0 top-8 -z-0 hidden h-0.5 w-full bg-border-light md:block"></div>

        {/* Connecting Line (Mobile) - 高度根據展開狀態調整 */}
        <div
          className={`absolute left-7 top-0 -z-0 w-0.5 bg-border-light transition-all duration-300 md:hidden ${
            isExpanded ? 'h-[calc(100%-3rem)]' : 'h-[7rem]'
          }`}
        />

        {/* Desktop: 顯示全部 6 步驟 */}
        <div className="hidden md:grid md:grid-cols-6 md:gap-2">
          {HERO_STEPS.map((step, index) => (
            <HeroStep
              key={step.id}
              {...step}
              index={index}
              isLast={index === HERO_STEPS.length - 1}
            />
          ))}
        </div>

        {/* Mobile: 可收合的時間軸 */}
        <div className="grid grid-cols-1 gap-3 md:hidden">
          {HERO_STEPS.slice(0, isExpanded ? HERO_STEPS.length : MOBILE_VISIBLE_STEPS).map(
            (step, index, arr) => (
              <HeroStep key={step.id} {...step} index={index} isLast={index === arr.length - 1} />
            )
          )}

          {/* 展開/收合按鈕 */}
          <button
            type="button"
            onClick={() => setIsExpanded(!isExpanded)}
            className="mt-1 flex items-center justify-center gap-2 rounded-xl border border-border-light bg-bg-soft px-4 py-2.5 text-sm font-bold text-text-muted transition-all hover:border-brand hover:text-brand active:scale-[0.98]"
          >
            {isExpanded ? (
              <>
                收合流程 <ChevronUp size={16} />
              </>
            ) : (
              <>
                查看完整流程 ({HERO_STEPS.length - MOBILE_VISIBLE_STEPS} 步驟){' '}
                <ChevronDown size={16} />
              </>
            )}
          </button>
        </div>
      </div>
    </HomeCard>
  );
}
